import React, { Fragment } from 'react'
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';

export default function ProductCard({title,des,cat,file,loc,price,showAll}) {

  let short_des = des.length>30 ?des.substring(0,15)+'...' :des;



  

  function getUrl()
  {
    return `https://source.unsplash.com/random/100×100/?${loc}`;
  }
    

  function openProduct()
  {
    console.log(`click ${title}`);
  }
  return (
  
    <Card className='card' onClick={openProduct}>

<Card.Header className='text-muted'>
        <header className='header-row'>
        <span className=''>{loc}</span>
        <span className=''>{cat}</span>
        </header>
    </Card.Header>
      
    <Card.Img variant="top" className='card-img' src={getUrl()}/>
    <Card.Body>
      <Card.Title>{title}</Card.Title>
      <Card.Subtitle className='text-muted'>{`$ ${price}`}</Card.Subtitle>
      <br/>
      <Card.Text>
       {short_des}
      </Card.Text>
      
      
    </Card.Body>


    {

    <Card.Footer>
        <footer className='row'>

        <Button className='btn-info col mx-1'>EDIT</Button>

        <Button className='btn-danger col mx-1'>DELETE</Button>

        </footer>

    </Card.Footer>
  }

  </Card>
  )
}
