import React, { useCallback, useEffect, useMemo, useReducer, useRef, useState } from 'react'
import { Button, InputGroup } from 'react-bootstrap'


import './auth.css'

const authType = {

    login:'login',
    signUp:'signUp',
};

function reducer(state,action)
{
    switch(action.type)
    {
        case authType.
        default:
            throw new Error('Invalid action request')
    }
}
export default function Auth() {

    const [state,dispatch]=useReducer(reducer,{});
    const [username,setUsername]=useState('');
    const [password,setPassword]=useState(''); 
    
    function onsubmitHandler(e)
    {
        e.preventDefault();
        console.log('form submited');

    }


    
    
//   let namelist =  useCallback(()=>{

//      return [username,username+" Bob" , username+" Great"];

//     },[username]);


   

  

    
   
    
    return (

    
        <form className='auth-form' onSubmit={onsubmitHandler}>
          

                <h3>Let's authenticate you </h3>
                <hr/>

                <label>Username</label>

                <input value={username} onChange={(e)=>{setUsername(e.target.value)}} className='auth-form-control' type="text" />

                    <br/>

                <label>Password</label>

                <input value={password} onChange={(e)=>{setPassword(e.target.value)}} className='auth-form-control' type="password" />

                <br/>
                <br/>

    
                <Button type='submit' className='auth-btn'> Login </Button>

           


        </form>
    )
}
