import React, { Fragment } from 'react'
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';

export default function ProductCard({title,des,cat,file,loc,price}) {


    const url = '';
    
  return (
  
    <Card style={{ width: '18rem' }}>
        
    <Card.Img variant="top" src={url}/>
    <Card.Body>
      <Card.Title>{title}</Card.Title>
      <Card.Subtitle className='text-muted'>{`Rs ${price}`}</Card.Subtitle>
      <Card.Text>
       {des}
      </Card.Text>
      <Button variant="primary">Barter</Button>
      
    </Card.Body>
    <Card.Footer className='text-muted'>
        <footer className='row'>
        <span className='col'>{loc}</span>
        <span className='col'>{cat}</span>
        </footer>
    </Card.Footer>
  </Card>
  )
}
