import React,{useState,useRef,useEffect} from "react";


export default function ProductForm(props)
    {

    const [title,setTitle]=useState("AVATAR");
    const [des,setDes]=useState("");
    const [price,setPrice]=useState(10);
    const [loc,setLoc]=useState("Jaipur");
      

    

    let prodList = useRef(['Title:"AVATAR 2']);

    useEffect(()=>{
      console.log('Title is rendering');
    },[]);


    function show(e)
    {
      e.preventDefault();
      try{
      let list = [...prodList.current];
      console.log(list);
      submitForm();
      }
      catch(err)
      {
        console.log(err);
      }
    }

    function submitForm()
    { 
        console.log('Submit the form ');
        console.log([title,des,price]);
        
    }
    
    return <form>

      <label>Title</label>

      <input value={title} onChange={(e)=>{setTitle((prev) => e.target.value)}} type="text"/>

      <label> Description </label>

      <input type="text" value={des} onChange={(e)=>{setDes((prev)=>e.target.value)}} />

      
      <label> Price </label>

      <input type="number" value={price} onChange={(e)=>{setPrice(e.target.value)}} />

      
      <br/>
      <br/>

    

      <label> Category </label>

      <select name="Category" id="Category">

        <option value="Electronic"> Electronic </option>
        <option value="Books"> Books </option>
        <option value="Other"> Other </option>

      </select>

      <label>Location</label>

      <input type="text" value={loc} onChange={(e)=>{setLoc()}} />

        <br/><br/>
      <button type="submit" onClick={show}> SHOW THE LIST </button>
    </form>
}
