import React,{useState,useRef,useEffect, useContext} from 'react'
import { Badge, Container } from 'react-bootstrap';
import { AuthContext } from '../../../context/auth_context';
import ProductCard from './product_card';

import './show_product.css'
export default function ShowProduct(props) {


    const auth=useContext(AuthContext);

    const all = false;


 
    
    console.log('username',auth.username);

    const prodsURL='myprods';
    let idx=0;

    let [prods,setProds] = useState([]);

    useEffect(()=>{

        

        let prod_list = JSON.parse(localStorage.getItem(prodsURL));

        if(all){
        setProds(prod_list);
        }

        else
        {
           const myProdList= prod_list.filter((prod)=>{

                return prod.creator ===  auth.username;
            });

            setProds(myProdList);
        }
        
  
      },[]);


  
  return (

    <Container>

        <h1 className='display-3 title'>My Products</h1>
        <br/>
        <div className='grid'>
       {

            

            prods.map(function (item){

                return <ProductCard key={idx++} {...item} />
            })
       } 

</div>
      

</Container>
  
  
    )
}
