import React,{useState,useRef,useEffect} from "react";


export default function ProductForm(props)
    {

    const [title,setTitle]=useState("AVATAR");
    const [des,setDes]=useState("This is a very good product");
    const [price,setPrice]=useState(10);
    const [loc,setLoc]=useState("Jaipur");
    const [cat,setCat]=useState("Books");
    

    

    let prodList = useRef(['Title:"AVATAR 2']);

    useEffect(()=>{
      console.log('Title is rendering');

      const items = JSON.parse(localStorage.getItem('items'));
      
      if(items)
      {
          setTitle(items[0]);
          setDes(items[1]);
          setPrice(items[2]);
          setLoc(items[3]);
          setCat(items[4]);


      }

    },[]);


    function submit(e)
    {
      e.preventDefault();
      
      submitForm();
    }

    function submitForm()
    { 
        console.log('Submit the form ');
        console.log([title,des,price,loc,cat]);

        let items = [title,des,price,loc,cat];

        localStorage.setItem('items',JSON.stringify(items));
        
    }
    
    return <form>
      <div className="form-group">

      <label>Title</label>

      <input className="form-control" value={title} onChange={(e)=>{setTitle((prev) => e.target.value)}} type="text"/>

      <label> Description </label>

      <input className="form-control"   type="text" value={des} onChange={(e)=>{setDes((prev)=>e.target.value)}} />

      
      <label> Price </label>

      <input  className="form-control" type="number" value={price} onChange={(e)=>{setPrice(e.target.value)}} />

      
      <br/>
      <br/>

    

      <label> Category </label>

      <select name="Category" id="Category"  value={cat} onChange={(e)=>{setCat(e.target.value)}}>

        <option value="Electronic"> Electronic </option>
        <option value="Books"> Books </option>
        <option value="Other"> Other </option>

      </select>

      <label>Location</label>

      <input type="text" value={loc} onChange={(e)=>{setLoc(e.target.value)}} />

        <br/><br/>
      <button type="submit" onClick={submit}> SHOW THE LIST </button>

      </div>
    </form>
}
