    return (

    
        <form className='auth-form' onSubmit={onsubmitHandler}>
          

                <h3>Let's authenticate you </h3>
                <hr/>

                <label>Username</label>

<input value={state.username} onChange={(e)=>{dispatch({type:authType.login,username: e.target.value})}} className='auth-form-control' type="text" />

                    <br/>

                <label>Password</label>

    <input value={state.password} onChange={(e)=>{dispatch({type:authType.login,password:e.target.value})}} className='auth-form-control' type="password" />

                <br/>
                <br/>

    
                <Button type='submit' className='auth-btn'> Login </Button>

           


        </form>
    )
}
