import React, { useState, useRef, useEffect, useContext } from "react";
import { Alert, Spinner } from "react-bootstrap";
import { Link, Redirect, useNavigate } from "react-router-dom";
import { AuthContext } from "../../../context/auth_context";
import "./product_form.css";
export default function ProductForm(props) {
  

  const auth=useContext(AuthContext);

  const prodsURL = "myprods";

  const [title, setTitle] = useState("");
  const [des, setDes] = useState("");
  const [price, setPrice] = useState(0);
  const [loc, setLoc] = useState("");
  const [cat, setCat] = useState("");
  const [file, setFile] = useState(null);
  const [isloading, setLoading] = useState(false);
  const [done, setDone] = useState(false);
  const navigate = useNavigate();

  let prodList = useRef(['Title:"AVATAR 2']);

  useEffect(() => {
    console.log("Title is rendering");

    const items = JSON.parse(localStorage.getItem("items"));

    const prod_list = JSON.parse(localStorage.getItem(prodsURL));

    console.log("PRODS LIST $$$$$$$$$$$$$$$$$$", prod_list);

    if (items) {
      setTitle(items[0]);
      setDes(items[1]);
      setPrice(items[2]);
      setLoc(items[3]);
      setCat(items[4]);

      setLoc("USA");
    }
  }, []);

  function submit(e) {
    e.preventDefault();

    setLoading(true);

    submitForm();

    setTimeout(() => {
      setLoading(false);
      setDone(true);

      setTimeout(() => {
        setDone(false);

        navigate("/showProd");
      }, 1000);
    }, 1000);
  }


  function submitForm() {
    console.log("submit the form ");

    let items = { title, des, price, loc, cat, file, creat:auth.username};

    console.log(items);

    let prod_list = JSON.parse(localStorage.getItem(prodsURL));

    if (prod_list == null) {
      prod_list = [];
    }

    prod_list.push(items);

    localStorage.setItem(prodsURL, JSON.stringify(prod_list));
  }





  return (
    <form className="form" onSubmit={submit}>
      <div className="form-group">
        <label className="text-white">Title</label>
        <input
          className="form-control"
          value={title}
          onChange={(e) => {
            setTitle((prev) => e.target.value);
          }}
          type="text"
        />
        <br />
        <label className="text-white"> Description </label>
        <input
          className="form-control"
          type="text"
          value={des}
          onChange={(e) => {
            setDes((prev) => e.target.value);
          }}
        />
        <br />
        <label className="text-white"> Price </label>
        <input
          className="form-control"
          type="number"
          value={price}
          onChange={(e) => {
            setPrice(e.target.value);
          }}
        />
        <br />
        <label className="text-white"> Category </label>
        <select
          className="form-control"
          name="Category"
          id="Category"
          value={cat}
          onChange={(e) => {
            setCat(e.target.value);
          }}
        >
          <option className="dropdown-item" value="Electronic">
            {" "}
            Electronic{" "}
          </option>
          <option className="dropdown-item" value="Books">
            {" "}
            Books{" "}
          </option>
          <option className="dropdown-item" value="Other">
            {" "}
            Other{" "}
          </option>
        </select>
        <br />
        <label className="text-white">Location</label>
        <input
          className="form-control"
          type="text"
          value={loc}
          onChange={(e) => {
            setLoc(e.target.value);
          }}
        />
        <br />
        <label className="text-white">Upload image</label>
        <input
          className="gap-l"
          type="file"
          onChange={(e) => {
            setFile(URL.createObjectURL(e.target.files[0]));
          }}
        />
        {file ? <img src={file} /> : <></>}
        <br /> <br />
        <section className="center">
          <button
            className="btn btn-primary px-3 py-1"
            type="submit"
            onClick={submit}
          >
            {" "}
            <span className="text-white">Submit</span>{" "}
          </button>

          {isloading ? (
            <Spinner className="mx-5 " animation="border" variant="danger" />
          ) : (
            <></>
          )}

          {done ? (
            <Alert className="mx-5 px-3 py-3" variant="success">
              Product added successfully
            </Alert>
          ) : (
            <></>
          )}
        </section>
      </div>
    </form>
  );
}
