        {prods.length === 0 && !isloa ? (
          <h5 className="text-muted">No products yet</h5>
        ) : (
          prods.map(function (item) {
            return (
              <ProductCard key={item.id} {...{ ...item, showAll, setProds }} />
            );
          })
        )}
      </div>
    </Container>
  );
}
