import React,{useState,useRef,useEffect} from "react";
import './product_form.css'
export default function ProductForm(props)
    {

    const [title,setTitle]=useState("AVATAR");
    const [des,setDes]=useState("This is a very good product");
    const [price,setPrice]=useState(10);
    const [loc,setLoc]=useState("Jaipur");
    const [cat,setCat]=useState("Books");
    const [file,setFile]=useState(null);

    

    let prodList = useRef(['Title:"AVATAR 2']);

    useEffect(()=>{
      console.log('Title is rendering');

      const items = JSON.parse(localStorage.getItem('items'));
      
      if(items)
      {
          setTitle(items[0]);
          setDes(items[1]);
          setPrice(items[2]);
          setLoc(items[3]);
          setCat(items[4]);


      }

    },[]);


    function submit(e)
    {
      e.preventDefault();
      
      submitForm();
    }

    function submitForm()
    { 
        console.log('Submit the form ');
        console.log([title,des,price,loc,cat]);

        let items = [title,des,price,loc,cat];

        localStorage.setItem('items',JSON.stringify(items));
        
    }
    
    return <form className="form">
      <div className="form-group">

      <label className="text-white">Title</label>

      <input className="form-control" value={title} onChange={(e)=>{setTitle((prev) => e.target.value)}} type="text"/>

      <br/>

      <label className="text-white"> Description </label>

      <input className="form-control"   type="text" value={des} onChange={(e)=>{setDes((prev)=>e.target.value)}} />

      <br/>
      
      <label className="text-white"> Price </label>

      <input  className="form-control" type="number" value={price} onChange={(e)=>{setPrice(e.target.value)}} />

      
      <br/>
      
    

      <label className="text-white"> Category </label>

      <select className="form-control" name="Category" id="Category"  value={cat} onChange={(e)=>{setCat(e.target.value)}}>

        <option className="dropdown-item" value="Electronic"> Electronic </option>
        <option className="dropdown-item" value="Books"> Books </option>
        <option className="dropdown-item" value="Other"> Other </option>

      </select>

      <br/>

      <label className="text-white">Location</label>

      <input className="form-control" type="text" value={loc} onChange={(e)=>{setLoc(e.target.value)}} />

    

      <label className="text-white" >Upload image</label> 
      
      <input className="gap-l" type="file" onChange={(e)=>{setFile(URL.createObjectURL(e.target.files[0]))}} />

    
    
      <img src={file}/>
        }
    

        <br/> <br/>

        <section className="center">
      <button className="btn btn-primary" type="submit" onClick={submit}> Submit  </button>
      </section>

      </div>
    </form>
}
