import React, { createContext } from 'react'
import { userDataCache } from '../global_data';


let userData = JSON.parse(localStorage.getItem(userDataCache));

if(userData === null)
{
    userData={ isLogin: false, login: () => { }, logout: () => { }, username: null };
}
export const AuthContext = createContext(userData);
