import React, { useCallback, useEffect, useMemo, useReducer, useRef, useState } from 'react'
import { Button, InputGroup } from 'react-bootstrap'


import './auth.css'

const basicStyle ={color:'#603fef'};

const bgStyle ={'background-color':'white'};

const authType = {

    login:'login',
    signUp:'signUp',
    usernameChange:'usernameChange',
    
};

const initState={
    username:'',
    password:'',
    islogin:true,
}

function reducer(state,action)
{
    if(action.type==authType.login)
    {
        let prevState={...state}; 

        if(action.username)
        {
            console.log('login  username' );
            prevState.username = action.username;
        }

        else
        {
            console.log('login  password' );
            prevState.password = action.password;
        }

        return prevState;
    }

    else if(action.type==authType.signUp)
    {

    }

    else if(action.type==authType.signUp)
    {

    }

    else
    {
        throw new Error('Inavlid actions request');
    }
   
}
export default function Auth() {

    const [state,dispatch]=useReducer(reducer,initState);

    
    function onsubmitHandler(e)
    {
        e.preventDefault();
        console.log('form submited');

    }


    
    
//   let namelist =  useCallback(()=>{

//      return [username,username+" Bob" , username+" Great"];

//     },[username]);


   

  

    
   
    
    return (

    
        <form className='auth-form' onSubmit={onsubmitHandler}>
          

                <h3>Let's authenticate you </h3>
                <hr/>

                <label>Username</label>

<input value={state.username} onChange={(e)=>{dispatch({type:authType.login,username: e.target.value})}} className='auth-form-control' type="text" />

                    <br/>

                <label>Password</label>

    <input value={state.password} onChange={(e)=>{dispatch({type:authType.login,password:e.target.value})}} className='auth-form-control' type="password" />

                <br/>
                <br/>

    
                <Button type='submit' className='auth-btn'> Login </Button>

           
            <br/>
                <a>
                
                <span className='link' >{state.islogin ?'Register' :'SignIn'}</span>
                , insted
                </a>
        </form>
    )
}
