import React, { useState, useRef, useEffect, useContext } from "react";
import { Badge, Container } from "react-bootstrap";
import { AuthContext } from "../../../context/auth_context";
import { requestHeaders } from "../../../global_data";
import { productApiRoute, userApiRoute } from "../../../routes";
import useHttp from "../../../shared/hooks/useHttp";
import Title from "../../../shared/title";
import ProductCard from "./product_card";

import "./show_product.css";
export default function ShowProduct({ showAll }) {
  const auth = useContext(AuthContext);

  const prodsURL = "myprods";
  let idx = 0;

  const [loading ,error,done,sendRequest] = useHttp();

  let [prods, setProds] = useState([]);

  useEffect(async () => {
    console.log("Fetching the Products");

    let prod_list=[];

    if (showAll) {
      
      try
      {
          const data = await sendRequest(`${productApiRoute}/all`,'GET',requestHeaders,null);
          prod_list=data.product;


      }

      catch(err)
      {
        
      }

    } else {

      try
      { 
        
          const data = await sendRequest(`${userApiRoute}/${auth.uid}`,'GET',requestHeaders,null);
          prod_list=data.product;


      }

      catch(err)
      {
        
      }

    }

  setProds(prod_list);


  }, [showAll]);

  return (
    <Container>
      <Title title={showAll ? "Browse Nearby Products" : "My Products"} />
      
      <br />
      
      <div className="grid">
        {prods.length === 0 ? (
          <h5 className="text-muted">You don't have any products yet</h5>
        ) : (
          prods.map(function (item) {
            return (
              <ProductCard key={item.id} {...{ ...item, showAll, setProds }} />
            );
          })
        )}
      </div>
    </Container>
  );
}
