    function handle()
    {
        
        
        change( (prev)=>{return prev+1;});
        console.log(x);
    }

    useEffect( ()=>{},[str] )
    return (
<React.Fragment>
        <div className="task">
            <h1 className="white">
                The value is {x}
            </h1>

            <button onClick={handle}>
                Increment 
            </button>
        </div>

<div className="task">
<h1 className="white">
    The value is {str}
</h1>

<button onClick={handle}>
    Increment 
</button>
</div>

</React.Fragment>
    );
}

export default Task;
