import React,{useState,useRef,useEffect} from "react";


export default function ProductForm(props)
    {

    let title="";
    let description="";
    let price=0;

    let prodList = useRef(['Title:"AVATAR 2']);

    useEffect(()=>{console.log(props.name)},[])


    function show(e)
    {
      e.preventDefault();
      try{
      let list = [...prodList.current];
      console.log(list);
      }
      catch(err)
      {
        console.log(err);
      }
    }

    function submitForm(e)
    { 
        e.preventDefault();

        console.log(title);
    }
    
    return <form>

      <label>Title</label>

      <input value={title} onc type="text" onSubmit={submitForm} />

      <label> Description </label>

      <input type="text"/>

      
      <label> Price </label>

      <input type="number"/>

      
      <br/>
      <br/>

      <label> Bargain Range </label>

      <input type="number"/>


      <label> Category </label>

      <select name="Category" id="Category">

        <option value="Electronic"> Electronic </option>
        <option value="Books"> Books </option>
        <option value="Other"> Other </option>

      </select>

      <label>Location</label>
      <input type="text"/>

        <br/><br/>
      <button type="submit" onClick={show}> SHOW THE LIST </button>
    </form>
}
