import { type } from '@testing-library/user-event/dist/type';
import React, { useCallback, useEffect, useMemo, useReducer, useRef, useState } from 'react'
import { Button, InputGroup } from 'react-bootstrap'


import './auth.css'

const basicStyle = { color: '#603fef' };

const bgStyle = { 'background-color': 'white' };

const authType = {

    login: 'login',
    signUp: 'signUp',
    usernameChange: 'usernameChange',
    passwordChange: 'passwordChange',
    nameChange:'nameChange',
    modeSwitch: 'modeSwitch',
    reset:'reset'
    
};

const initState = {
    username: '',
    password: '',
    name:'',
    islogin: true,
}

function reducer(state, action) {
    let prevState = { ...state };

    if (action.type == authType.usernameChange) {

        prevState.username = action.username;
        return prevState;
    }

    else if (action.type == authType.passwordChange) {

     
        prevState.password = action.password;
        return prevState;
    }

    else if (action.type == authType.nameChange) {

     
        prevState.name = action.name;
        return prevState;
    }

    else if (action.type == authType.modeSwitch) {
        prevState.islogin = !prevState.islogin;
        return prevState;
    }

    else {
        throw new Error('Inavlid actions request');
    }

}
export default function Auth() {

    const [state, dispatch] = useReducer(reducer, initState);


    function onsubmitHandler(e) {
        e.preventDefault();
        console.log('form submited');

    }




    //   let namelist =  useCallback(()=>{

    //      return [username,username+" Bob" , username+" Great"];

    //     },[username]);









    return (


        <form className='auth-form' onSubmit={onsubmitHandler}>


            <h3> {state.islogin? "Welcome back" :"Regsiter"} </h3>
            <hr />

            {
                !state.islogin && <>

                    <label>Name</label>
    <input className='auth-form-control' value={state.name} onChange={(e)=>{dispatch({type:authType.nameChange,name:e.target.value})}} type="text"/>
                </>
            }

            <br/>
            <label>Username</label>

            <input value={state.username} onChange={(e) => { dispatch({ type: authType.usernameChange, username: e.target.value }) }} className='auth-form-control' type="text" />

            <br />

            <label>Password</label>

            <input value={state.password} onChange={(e) => { dispatch({ type: authType.passwordChange, password: e.target.value }) }} className='auth-form-control' type="password" />

            <br />
            <br />


                <div className='row'>
            <Button  type='submit' className='auth-btn col'>{state.islogin? 'Login' :'Register'} </Button>


            <Button  className='btn-secondary mx-1 col' type="reset">Reset</Button>


            </div>


            <br />
            <a>

                <span onClick={(e)=>{dispatch({type:authType.modeSwitch})}} className='link' >{state.islogin ? 'Register' : 'SignIn'}</span>
                , insted
            </a>
        </form>
    )
}
