import { type } from '@testing-library/user-event/dist/type';
import React, { useCallback, useEffect, useMemo, useReducer, useRef, useState } from 'react'
import { Button, InputGroup } from 'react-bootstrap'


import './auth.css'

const authType = {

    login:'login',
    signUp:'signUp',
};

function reducer(state,action)
{
    switch(action.type)
    {
        case authType.login:
            console.log('login');
            return state;

        case authType.signUp:
            console.log('Signup');
            return state;


        
        default:
            throw new Error('Invalid action request')
    }
}
export default function Auth() {

    const [state,dispatch]=useReducer(reducer,{username:'',password:''});

    
    function onsubmitHandler(e)
    {
        e.preventDefault();
        console.log('form submited');

    }


    
    
//   let namelist =  useCallback(()=>{

//      return [username,username+" Bob" , username+" Great"];

//     },[username]);


   

  

    
   
    
    return (

    
        <form className='auth-form' onSubmit={onsubmitHandler}>
          

                <h3>Let's authenticate you </h3>
                <hr/>

                <label>Username</label>

    <input value={state.username} onChange={(e)=>{dispatch({type:authType.login,username: e.target.value})}} className='auth-form-control' type="text" />

                    <br/>

                <label>Password</label>

        <input value={state.password} onChange={(e)=>{dispatch({type:authType.login,password:e.target.value})}} className='auth-form-control' type="password" />

                <br/>
                <br/>

    
                <Button type='submit' className='auth-btn'> Login </Button>

           


        </form>
    )
}
